#include "ADGConnectionForIOS.h"#include "ADGConnectionObjC.h"static ADGConnectionObjC *adgc;static int defaultx;static int defaulty;void ADGConnectionForIOS::initADG(char *adid , char *type , int x , int y , void (*func)(const char * , const char *)){    initADGAction((char *)adid , (char *)type , x , y , 0 , 0 , 1.0 , false , true , func);    return;}void ADGConnectionForIOS::initADGWithEarlyOS(char *adid , char *type , int x , int y , bool landscape , void (*func)(const char * , const char *)){    initADGAction((char *)adid , (char *)type , x , y , 0 , 0 , 1.0 , true , landscape , func);}void ADGConnectionForIOS::initADGWithWH(char *adid , char *type , int x , int y, int width , int height , void (*func)(const char * , const char *)){    initADGAction((char *)adid , (char *)type , x , y , width , height , 1.0 , false , true , func);}void ADGConnectionForIOS::initADGWithWHAndEarlyOS(char *adid , char *type , int x , int y , int width , int height , bool landscape , void (*func)(const char * , const char *)){    initADGAction((char *)adid , (char *)type , x , y , width , height , 1.0 , true , landscape , func);}void ADGConnectionForIOS::initADGWithScale(char *adid , char *type , int x , int y, int width , int height , double scale , void (*func)(const char * , const char *)){    initADGAction((char *)adid , (char *)type , x , y , width , height , scale , false , true , func);}void ADGConnectionForIOS::initADGAction(char *adid , char *type , int x , int y , int width , int height , double scale , bool earlyOS , bool landscape ,  void (*func)(const char * , const char *)){    if(!adgc){        adgc = [[ADGConnectionObjC alloc] init];    }        defaultx = x;    defaulty = y;        [adgc initADG:[NSString stringWithCString:adid encoding:NSUTF8StringEncoding] type:[NSString stringWithCString:type encoding:NSUTF8StringEncoding] x:x y:y width:width height:height scale:(float)scale earlyOS:earlyOS landscape:landscape func:func];        return;}void ADGConnectionForIOS::showADG(){    [adgc showADG];    [adgc resumeADG];}void ADGConnectionForIOS::hideADG(){    [adgc hideADG];    [adgc pauseADG];}void ADGConnectionForIOS::pauseADG(){    [adgc pauseADG];}void ADGConnectionForIOS::resumeADG(){    [adgc resumeADG];}void ADGConnectionForIOS::changeLocationADG(int x , int y){    [adgc changeLocationADG:x y:y];}void ADGConnectionForIOS::moveDefaultLocationADG(){    [adgc changeLocationADG:defaultx y:defaulty];}void ADGConnectionForIOS::finishADG(){    [adgc hideADG];    [adgc release];    adgc = nil;}